var carta1 = {
  nome: "Audi A3",
  atributos: {
    cilindrada: 1984,
    motor: 16,
    potencia: 220
  }
};

var carta2 = {
  nome: "BMW X4",
  atributos: {
    cilindrada: 1997,
    motor: 16,
    potencia: 245
  }
};

var carta3 = {
  nome: "Fiat Palio",
  atributos: {
    cilindrada: 999,
    motor: 8,
    potencia: 75
  }
};

var cartas = [carta1, carta2, carta3];
var cartaMaquina, cartaJogador;

function sortearNumero() {
  return parseInt(Math.random() * 3);
}

function sortearCarta() {
  var numeroCartaMaquina = sortearNumero();
  cartaMaquina = cartas[numeroCartaMaquina];
  var numeroCartaJogador;
  do {
    numeroCartaJogador = sortearNumero();
  } while (numeroCartaMaquina == numeroCartaJogador);
  cartaJogador = cartas[numeroCartaJogador];
  console.log(cartaMaquina);
  console.log(cartaJogador);

  var botaoSortear = document.getElementById("btnSortear");
  botaoSortear.disabled = true;
  var botaoJogar = document.getElementById("btnJogar");
  botaoJogar.disabled = false;

  exibirCartaJogador();
}

function obtemAtributoSelecionado() {
  var pegaOpcao = document.querySelector('input[name="atributo"]:checked');
  if (pegaOpcao == null) return "";
  else return pegaOpcao.value;
}

function jogar() {
  var valorAtributoSelecionado = obtemAtributoSelecionado();
  if (valorAtributoSelecionado == "") {
    alert("É necessário escolher um atributo!");
    return;
  }
  exibirCartaMaquina();
  var valorCartaMaquina = cartaMaquina.atributos[valorAtributoSelecionado];
  var valorCartaJogador = cartaJogador.atributos[valorAtributoSelecionado];
  var divResultado = document.getElementById("resultado");
  if (valorCartaMaquina > valorCartaJogador)
    divResultado.innerHTML = "<p class='resultado-final'>Você perdeu</p>";
  else if (valorCartaMaquina < valorCartaJogador)
    divResultado.innerHTML = "<p class='resultado-final'>Você venceu</p>";
  else
    divResultado.innerHTML =
      "<p class='resultado-final'>Você empatou com a máquina</p>";
}

function exibirCartaJogador() {
  var divCartaJogador = document.getElementById("carta-jogador");
  divCartaJogador.style.backgroundImage = `url(${cartaJogador.imagem})`;

  var moldura =
    '<img src="https://www.alura.com.br/assets/img/imersoes/dev-2021/card-super-trunfo-transparent-ajustado.png" style=" width: inherit; height: inherit; position: absolute;">';
  var tagHTML = "<div id='opcoes' class='carta-status'>";
  var opcoesTexto = "";
  for (var atributo in cartaJogador.atributos) {
    opcoesTexto +=
      "<input type='radio' name='atributo' value='" +
      atributo +
      "'>" +
      atributo +
      " " +
      cartaJogador.atributos[atributo] +
      "</br>";
    var nome = `<p class="carta-subtitle">${cartaJogador.nome}</p>`;

    divCartaJogador.innerHTML =
      moldura + nome + tagHTML + opcoesTexto + "</div>";
  }
}

function exibirCartaMaquina() {
  var divCartaMaquina = document.getElementById("carta-maquina");
  divCartaMaquina.style.backgroundImage = `url(${cartaMaquina.imagem})`;

  var moldura =
    '<img src="https://www.alura.com.br/assets/img/imersoes/dev-2021/card-super-trunfo-transparent-ajustado.png" style=" width: inherit; height: inherit; position: absolute;">';
  var tagHTML = "<div id='opcoes' class='carta-status'>";
  var opcoesTexto = "";
  for (var atributo in cartaMaquina.atributos) {
    opcoesTexto +=
      "<p type='text' name='atributo' value='" +
      atributo +
      "'>" +
      atributo +
      " " +
      cartaMaquina.atributos[atributo];
    var nome = `<p class="carta-subtitle">${cartaMaquina.nome}</p>`;

    divCartaMaquina.innerHTML =
      moldura + nome + tagHTML + opcoesTexto + "</div>";
  }
}

